import streamlit as st

st.set_page_config(
    layout="wide",
    page_title = "Players",
    page_icon =  " data:image/png;base64,")

df_data = st.session_state["data"]

clubes = df_data["Club"].value_counts().index
club = st.sidebar.selectbox("Clube", clubes)

df_players = df_data[(df_data["Club"] == club)]

Players = df_players["Name"].value_counts().index
Player = st.sidebar.selectbox("Jogador", Players)

Player_stats = df_data[df_data["Name"] == Player].iloc[0]

st.image(Player_stats["Photo"])
st.title(Player_stats["Name"])

st.markdown(f"**Clube:** {Player_stats['Club']}")
st.markdown(f"**Posição:** {Player_stats['Position']}")

col1, col2, col3, col4 = st.columns(4)

col1.markdown(f"**Idade:** {Player_stats['Age']} anos")
col2.markdown(f"**Altura:** {Player_stats['Height(cm.)']/100}m")
col3.markdown(f"**Peso:** {Player_stats['Weight(lbs.)']*0.453:.2f}Kg")

st.divider()

st.subheader(f"**Overall:** {Player_stats['Overall']}")
st.progress(int(Player_stats["Overall"]))

col1, col2, col3, col4 = st.columns(4)

col1.metric(label = "Valor de mercado", value=f'£{Player_stats["Value(£)"]:,}')
col2.metric(label = "Remuneração Semanal", value=f'£{Player_stats["Wage(£)"]:,}')
col3.metric(label = "Cláusula de recisão", value=f'£{Player_stats["Release Clause(£)"]:,}')